declare namespace Analytics {
  interface ConstructorParams {
    sessionId?: string;
    partnerId?: string;
    clientVersion: string;
    source: string;
    componentId: string;
    name: string;
    options?: { server: boolean };
  }

  interface SessionInfo {
    sessionId: string;
    connectionId: string;
    partnerId: string;
  }

  export class OTKAnalytics {
    constructor(params: ConstructorParams)
    /**
     * Set additional session info to be included with logs
     * @param info {SessionInfo}
     */
    public addSessionInfo(info: SessionInfo): void
    /**
     * Log an event
     * @param data {Object}
     */
    public logEvent(data: { [key: string]: any }): void
  }
}

declare module "opentok-solutions-logging" {
  export = Analytics.OTKAnalytics;
}